const chalk = require('chalk')
function install() {
    const {exec} = require('child_process')
    exec('npm i spapi-wrapper spapi-wrapper-v2', (error, stdout, stderr) => {
        if (!error) {
            console.log(stdout, stderr)
            setTimeout(() => {
                console.log(chalk.yellowBright("Installed all packages. Thanks for using SP-API"))
            }, 1000)
        } else {
            console.log(chalk.red(error))
        }
    })
    console.log(chalk.yellow("Installing all packages..."))
}
function uninstall() {
    const {exec} = require('child_process')
    exec('npm un spapi-wrapper spapi-wrapper-v2', (error, stdout, stderr) => {
        if (!error) {
            console.log(stdout, stderr)
            setTimeout(() => {
                console.log(chalk.redBright("Uninstalled all packages. Thanks for using SP-API"))
            }, 1000)
        } else {
            console.log(error)
        }
    })
    console.log(chalk.red("Uninstalling all packages..."))
}

module.exports = {
    install,
    uninstall
}